const modalReducer = (state = false, action) => {
    switch(action.type){
        case 'OPEN_LOGIN': {
            return !state;
        }
        case 'CLOSE_LOGIN': {
            return false;
        }
        default:{
            return state;
        }
    }
}

export default modalReducer;
