import styled from 'styled-components'

export const Button = styled.a`
border-radius: 50px;
background: #304ffe;
white-space: nowrap;
padding: 14px 48px;
color: #fff;
font-size: 16px;
outline: none;
border: none;
cursor: pointer;
display: flex;
justify-content: center;
align-items: center;
transition: all 0.2s ease-in-out;
text-decoration: none !important;
&:hover{
  transition: all 0.2s ease-in-out;
  background: #fff;
}

`
